INSERT INTO `iot_product`.`t_pm_module` (`id`, `module_name`, `module_name_en`, `firmware_type`, `firmware_flag`, `firmware_id`, `status`, `img_url`, `file_url`, `remark`, `created_at`, `updated_by`, `updated_at`, `deleted_at`, `file_name`, `default_version`, `firmware_key`) VALUES (1706914806461530112, 'esp8826', '8826', '2', 'TG12F', 4887421905079795712, 1, 'https://osspublic.aithinker.com/module/63c75642-73e5-4923-b8bc-7a57f1a1c09e.png', 'https://osspublic.aithinker.com/moduleDoc/9fcc0897-3b90-49d0-bd7d-2d16569f4269.pdf', '新华社北京4月5日电题：中国持续增绿为世界交出“绿色答卷”新华社记者张文静 陈雍容 周文其农谚有云：“植树造林，莫过清明。”每逢此时节，从南到北，从党和国家领导人到普通民众，全民义务植树在中国蓬勃开展，绿色发展已成为全社会的共识和行动自觉。森林是陆地生态的主体，是人类生存的根基。中国总体缺林少绿、生态脆弱，一直坚持数量质量并重，深入实施大规模国土绿化行动。河北塞罕坝机械林场内的望海楼（资料照片）。新华社记者 牟宇 摄的十八大以来，中国每年造林面积都在1亿亩以上开展全民义务植树是推进国土绿化的有效途径。1981年，中国通过《关于开展全民义务植树运动的决议》。次年，国务院颁布《关于开展全民义务植树运动的实施办法》。从1982年植树节开始至2021年12月，中国适龄公民累计有175亿人次参加义务植树，累计植树781亿株（含折算）。爱绿植绿护绿的文明风尚、植树造林的责任意识持续增强提升。近年来，全国绿化委员会出台《全民义务植树尽责形式管理办法（试行）》，将尽责形式扩展到造林绿化、抚育管护、认种认养、捐资捐物、志愿服务等八大类50多种。中国不断创新义务植树方式方法，“互联网+全民义务树”全面推开', '2023-04-07 10:21:32', 0, '2024-01-30 11:48:04', NULL, '串口协议文档(1).pdf', '1.0.2', '');
