INSERT INTO `iot_product`.`t_pm_thing_model_item` (`id`, `product_type_id`, `dpid`, `identifier`, `name`, `rw_flag`, `data_type`, `properties`, `mark`, `required`) VALUES (6295330402492055552, 7541468689632165888, 25, 'update_all_finger', '同步所有指纹编号', 'READ', 'TEXT', '', '满足以下所有条件时，选择dpid。\n1、门锁具备指指纹解锁的方式\n2、门锁能够通过网络，将本地添加的解锁方式同步到服务器\n使用方式：当门锁本地的指纹解锁方式发生变化，新增/删除，则触发同步所有指纹的dp，将门锁本地所有的指纹解锁id同步到云端注意：每次向上同步的时候，需要使用全量同步，把更新后本地所具备的所有的id都同步到云端', 0);
INSERT INTO `iot_product`.`t_pm_thing_model_item` (`id`, `product_type_id`, `dpid`, `identifier`, `name`, `rw_flag`, `data_type`, `properties`, `mark`, `required`) VALUES (6295330401934213120, 7541468689632165888, 26, 'update_all_password', '同步所有密码编号', 'READ', 'TEXT', '', '满足以下所有条件时，选择dpid。\n1、门锁具备密码解锁的方式\n2、门锁能够通过网络，将本地添加的解锁方式同步到服务器\n3、此处所指的密码是在门锁上创建的普通的密码，不包括通过app创建的临时密码\n使用方式：当门锁本地的密码解锁方式发生变化，新增/删除，则触发同步所有密码的dp，将门锁本地所有的密码解锁id同步到云端注意：每次向上同步的时候，需要使用全量同步，把更新后本地所具备的所有的id都同步到云端', 0);
