// 获取当前租户的授权设备总数
func (s *OpenAuthQuantitySvc) GetCountByTenantId(req *proto.OpenAuthQuantityFilter) (int64, error) {
	var err error
	tenantId, _ := metadata.Get(s.Ctx, "tenantid")
	if tenantId == "" {
		return 0, errors.New("租户ID获取失败.")
	}
	t := orm.Use(iotmodel.GetDB()).TOpenAuthQuantity
	do := t.WithContext(context.Background()).Where(t.TenantId.Eq(tenantId))

	var totalResult []struct {
		Total int64
	}

	err = do.Select(t.AuthQuantity.Sum().As("total")).Group(t.TenantId).Scan(&totalResult)
	if err != nil {
		return 0, err
	}
	var total int64 = 0
	for _, t := range totalResult {
		total += t.Total
	}
	return total, nil
}
